def mean_var_conf(x,x2):
    x = array(x)
    x2 = array(x2)
    var = x2 - x**2
    sigma = sqrt(var)
    xy = []
    for i in range(6):
        for j in range(6-i):
            xy.append("$x^"+str(i)+"y^"+str(j)+"$")
    for xy_i,mean,sigma_i in zip(xy,x,sigma):
        print("%-7s && %-10.6f && %-10.6f && [%.6f,%.6f] \\\\" % (xy_i,mean,sigma_i*sigma_i,mean-sigma_i,mean+sigma_i))

